DataSet.ClientEvent:
var id = self.getData("id");
$("a").attr("target", "doc_" + id);
var sources = self.getData().get("sources");
if (sources.entityCount == 0) {
	sources.createChild({ label: "(未定义源文件)" });
}

DataColumn.ClientEvent:
var t, sourceId = arg.data.get("id"), label = arg.data.get("label"), path = arg.data.get("path");
if (path) {
	t = $.template("<div style='padding:4px'><a target='_blank' href='com.bstek.dorado.sample.ViewSource.d?sourceId=\${sourceId}'>\${label}</a><br>" +
			"<label class='source-path'>\${path}</label><div>");
}
else {
	t = $.template("<div class='source-path' style='padding:4px'>\${label}</label><div>");
}
$(arg.dom).empty().append(t, { sourceId:sourceId, path:path, label:label }).css("background", "white");
