Button.ClientEvent:
var taskNo = self.get("userData") || 0;
self.set("userData", ++taskNo);
var taskId = dorado.util.TaskIndicator.showTaskIndicator("测试后台任务" + taskNo);
setTimeout(function() {
	dorado.util.TaskIndicator.hideTaskIndicator(taskId);
}, Math.random() * 3000 + 3000);

var taskNo = self.get("userData") || 0;
self.set({
	disabled: true,
	userData: ++taskNo,
});
var taskId = dorado.util.TaskIndicator.showTaskIndicator("测试主任务" + taskNo, "main");
setTimeout(function() {
	dorado.util.TaskIndicator.hideTaskIndicator(taskId);
	self.set("disabled", false);
}, Math.random() * 500 + 500);

var notifyNo = self.get("userData") || 0;
self.set("userData", ++notifyNo);
dorado.widget.NotifyTipManager.notify("通知信息" + notifyNo);

var notifyNo = self.get("userData") || 0;
self.set("userData", ++notifyNo);
dorado.widget.NotifyTipManager.notify("通知信息" + notifyNo, {
	caption: "<重要通知>",
	icon: "INFO"
});
